import React, { Component } from 'react';
import Todo from './Todo';
import NewTodoForm from './NewTodoForm';

class TodoList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            todo : [{task: "Chu Duc Viet"} , {task : "Tran thi tham"}]
        }
    }
   render() {
       const listTodo = this.state.task.map(item => {
           return (
               <Todo task={item.task} />
           )
       })
       return(
           <div>
               
               <NewTodoForm />
               <ul>
                   {listTodo}
               </ul>
           </div>
       )
   }
}
export default TodoList;
