import React, { Component } from 'react';
import Todo from './Todo';

class TodoList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            todos: [{ task: "Chu Duc Viet"}, {task : "tran thi anh"}]
        }
    }   
    render() {
        const listItem = this.state.todos.map(item => {
            return <Todo task={item.task} /> // task là props => gán array vào listItem để hiện thị ra array todos
        })
    
        return (
            <div>
                <h1>Todo List</h1>
                <ul>
                    { listItem }
                </ul>
            </div>
        )
    }
}


export default TodoList;
