import React, { Component } from 'react';
import Todo from './Todo';
import NewTodoForm from './NewTodoForm';

class TodoList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            todos: [
                { task: "Chu Duc Viet"}, {task : "tran thi anh"}]
        }
        this.create = this.create.bind(this);
    }

    create(newTodo) {
        this.setState({
            todos : [...this.state.todos, newTodo]
        })
    }
    render() {
        const listItem = this.state.todos.map(item => {
            return <Todo task={item.task} /> // task là props => gán array vào listItem để hiện thị ra array todos
        })
        return (
            <div>
                <h1>Todo List</h1>
                <NewTodoForm CreateTodo={this.create} />
                <ul>
                    { listItem }
                </ul>
            </div>
        )
    }
}
export default TodoList;
