import React, { Component } from 'react';

class NewTodoForm extends Component {
    constructor(props) {
        super(props);
        this.state = { task: "" };
    }
   render() {
       return(
           <form>
               <label htmlFor="task">New Todo</label>
               <input type="text" placeholder="Enter name" id="task" value={this.state.task} />
       </form>
       )
   }
}


export default NewTodoForm;
