import React, { Component } from 'react';
import Todo from 'Todo';

class TodoList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            todos: [{ task: "Chu Duc Viet"}, {task : "tran thi anh"}]
        }
    }   
    
 
    render() {

        const listItem = this.state.todos.map(item => {
            return <Todo task={item} />
        })
    
        return (
            <div>
                <h1>Todo List</h1>

                <ul>
                    <li>To do 1</li>
                    <li>To do 2</li>
                </ul>
            </div>
        )
    }
}


export default TodoList;
