import React, { Component } from 'react';
import TodoList from './TodoList';

class NewTodoForm extends Component {
    constructor(props) {
        super(props)
        this.state = {
            task : ""
        }
        
    }
    render() {
        return(
            <form>
                <label>New Todo</label>
                <input type="text" placeholder="Enter new todo" id="task" value={this.state.state} />
                <button>Add</button>
            </form>
        )
    }
}


export default NewTodoForm;
