import React, { Component } from 'react';
import Todo from './Todo';
import NewTodoForm from './NewTodoForm';

class TodoList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            task : ["Chu Duc Viet" , "Tran thi Tham"]
        }
    }
   render() {
       const listTodo = this.state.task.map(item => {
           return (
               <Todo task={item} />
           )
       })
       return(
           <div>
               <ul>
                   {listTodo}
               </ul>
           </div>
       )
   }
}
export default TodoList;
